import React, { useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';
import axios from 'axios';

const AuthRedirect = () => {
  const [searchParams] = useSearchParams();
  const [message, setMessage] = useState("Exchanging code for token...");
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    const code = searchParams.get("code");

    if (code) {
      axios.post('http://localhost:5000/api/instagram/token', { code })
        .then(res => {
          const accessToken = res.data.access_token;
          setMessage("Access token received successfully!");

          // ðŸ”¹ Fetch Instagram profile
          axios.post('http://localhost:5000/api/instagram/profile', { access_token: accessToken })
            .then(profileRes => {
              setProfile(profileRes.data);
              setMessage(`Welcome, ${profileRes.data.username} (${profileRes.data.account_type})`);
            })
            .catch(err => {
              console.error("Profile fetch error:", err);
              setMessage("Failed to fetch profile");
            });

          // ðŸ”¹ Optional: Fetch media feed
          axios.post('http://localhost:5000/api/instagram/media', { access_token: accessToken })
            .then(mediaRes => {
              console.log("User Media Feed:", mediaRes.data);
            })
            .catch(err => {
              console.error("Media fetch error:", err);
            });

        })
        .catch(err => {
          setMessage("Failed to exchange token");
          console.error(err);
        });
    } else {
      setMessage("No code found in URL");
    }
  }, [searchParams]);

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>{message}</h2>
      {profile && (
        <div style={{ marginTop: "20px" }}>
          <p><strong>User ID:</strong> {profile.id}</p>
          <p><strong>Username:</strong> {profile.username}</p>
          <p><strong>Account Type:</strong> {profile.account_type}</p>
        </div>
      )}
    </div>
  );
};

export default AuthRedirect;
